import { Component, OnInit } from '@angular/core';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-tab4',
  templateUrl: './tab4.page.html',
  styleUrls: ['./tab4.page.scss'],
})
export class Tab4Page implements OnInit {
  inputValue: string = "";
  max: any;
  constructor(private alertController: AlertController) { }

  ngOnInit() {
  }

  showMax() {
    var a = parseInt(this.inputValue, 10);
    this.max = a;
    if(!a) {
      this.showFail();
    } else {
      this.alertMax();
      console.log(a);
    }
    // console.log(a);

  }

  async alertMax() {
    const alert = await this.alertController.create({
      header: '1 Rep Max!',
      subHeader: 'Your 1 RM is: ' + this.max,
      buttons: [
        {
          text: 'Cancel',
          role: 'cancel',
          handler: () => {
            console.log('Alert canceled');
          },
        },
        {
          text: 'OK',
          role: 'confirm',
          handler: () => {
            console.log('Alert confirmed');
          },
        },
      ],
    });

    await alert.present();
  }

  async showFail() {
    const alert = await this.alertController.create({
      header: 'Enter a number!',
      buttons: [
        {
          text: 'OK',
          role: 'confirm',
          handler: () => {
            console.log('Alert confirmed');
          },
        },
      ],
    });

    await alert.present();
  }

}
