import { Component, OnInit, ViewChild } from '@angular/core';
import { Storage } from '@ionic/storage';
import { ActivatedRoute, Router, RouterEvent, NavigationEnd } from '@angular/router';
import { StorageItem } from '../shared/storage-item';
import { Platform, IonContent } from '@ionic/angular';
import { Filesystem, Directory, Encoding } from '@capacitor/filesystem';
import { File } from '@awesome-cordova-plugins/file/ngx';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage implements OnInit {
  @ViewChild(IonContent) private content: IonContent;
  list: StorageItem[];
  stringlist: string;
  subscription: any;
  constructor(private storage: Storage, private router: Router,
    private platform: Platform,
    private file: File) { }
  ngOnInit(): void {



    this.router.events.subscribe(async (e: any) => {
      // If it is a NavigationEnd event re-initalise the component
      if (e instanceof NavigationEnd) {
        this.list = await this.storage.get('notes') as StorageItem[];
        console.log(this.list);
      }
    });
  }


  ionViewDidEnter(){
    this.subscription = this.platform.backButton.subscribe(()=>{
        navigator['app'].exitApp();
    });
}

ionViewWillLeave(){
    this.subscription.unsubscribe();
}

  isEmpty() {
    return this.list === undefined || this.list === null;
  }

  trackElement(item) {
    return item.key;
  }

  async updateItem(item) {
    let it = this.list.find((element) => {
      return element === item;
    });
    this.router.navigate(['/edit-note'], {state: {obj: item}});

  }

  scrollToTop() {
    this.content.scrollToTop(500);
  }

  scrollToBottom() {
    this.content.scrollToBottom(500);
  }

  turntostr() {
    this.file.checkDir(this.file.dataDirectory, 'mydir').then(_ => console.log('Directory exists')).catch(err => console.log("Directory doesn't exist"));  
  }

  save() {
    console.log(this.list);
    this.stringlist = JSON.stringify(this.list);
    console.log(this.stringlist);
    const fileName = new Date().getTime() + '.txt';

    console.log(fileName);
  }

  load() {
    this.list = JSON.parse(this.stringlist);
  }


}
